import React, {useState} from 'react'
import PropTypes from 'prop-types'
import {useHistory} from 'react-router-dom'


function AddProduct({onAdd}){  
    let history = useHistory();
    const [ValueInput, setValueInput] = useState({});
    function onHandleChange(e){
        const { name, image, price, value } = e.target;
        setValueInput({
            ...ValueInput,
            [name]: e.target.value,
            [image]: e.target.value,
            [price]: e.target.value
        });
    }
    function onHandleSubmit(e){
        e.preventDefault();
        onAdd(ValueInput)
        history.push('/admin/products')
    }
    return (
        <div>
                <div className="content-wrapper">
                <div className="card">
                    <div className="card-header">
                        <h3 className="card-title">Thêm sản phẩm</h3>
                        <br />
                        <form onSubmit="{onHandleSubmit}">
                            <div className="form-group">
                                    <label htmlFor="inputProductName">Tên sản phẩm</label>
                                    <input name="name" type="text" className="form-control" id="inputProductName" onChange={onHandleChange}/>
                            </div>
                            <div className="form-group">
                                    <label htmlFor="inputProductImage">Ảnh sản phẩm</label>
                                    <input name="image" type="text" className="form-control" id="inputProductImage" onChange={onHandleChange}/>
                            </div>
                            <div className="form-group">
                                    <label htmlFor="inputProductPrice">Gía sản phẩm</label>
                                    <input name="price" type="text" className="form-control" id="inputProductPrice" onChange={onHandleChange}/>
                            </div>
                            <button type="submit" className="btn btn-primary" onClick={onHandleSubmit}>Submit</button>
                        </form>
                    </div>
                </div>
                </div>
            
        </div>
    )
}

AddProduct.propTypes = {

}

export default AddProduct
