import React, {useState} from 'react'
import PropTypes from 'prop-types'
import { useParams, Link } from 'react-router-dom'
import {useHistory} from 'react-router-dom'
const EditProduct = ({ products, onAdd }) => {
    let { id } = useParams();
    const product = products.find(product => product.id == id);
    let history = useHistory();
    const [ValueInput, setValueInput] = useState({});
    function onHandleChange(e){
        const { name, image, price, value } = e.target;
        setValueInput({
            ...ValueInput,
            [name]: e.target.value,
            [image]: e.target.value,
            [price]: e.target.value
        });
    }
    function onHandleSubmit(e){
        e.preventDefault();
        onAdd(ValueInput)
        history.push('/admin/products')
    }
    return (
        <div>
            <div className="content-wrapper">
                <div className="card">
                    <div className="card-header">
                        <h3 className="card-title">Sửa sản phẩm</h3>
                        <br />
                        <form>
                            <div className="form-group">
                                    <label htmlFor="inputProductName">Tên sản phẩm</label>
                                    <input value={product.name} name="name" type="text" className="form-control" id="inputProductName"/>
                            </div>
                            <div className="form-group">
                                    <label htmlFor="inputProductImage">Ảnh sản phẩm</label>
                                    <input value={product.image} name="image" type="text" className="form-control" id="inputProductImage"/>
                            </div>
                            <div className="form-group">
                                    <label htmlFor="inputProductPrice">Gía sản phẩm</label>
                                    <input value={product.price} name="price" type="text" className="form-control" id="inputProductPrice"/>
                            </div>
                            <button type="submit" className="btn btn-primary" onClick={onHandleSubmit}>Submit</button>
                        </form>
                    </div>
                </div>
                </div>
        </div>
    )
}

EditProduct.propTypes = {

}

export default EditProduct
