import React, {useState} from 'react'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Main from '../pages/layouts/Main'
import MainAdmin from '../pages/layouts/MainAdmin'
//Admin
import Dashboard from '../pages/views/Admin/Dashboard'
import ProductsManager from '../pages/views/Admin/Products'

//Views
import About from '../pages/views/Main/About'
import Home from '../pages/views/Main/Home'
import AddProduct from '../pages/views/Admin/AddProduct';
import dataFake from '../dataFake'


const Routers = ({ product, onRemove }) => {
    const onHandleRemove = (id) => {
        onRemove(id)
    }
    const [products, setProduct] = useState(dataFake);
    const onHanleRemove = (id) =>{
      let result = [...products];
      result.splice(id, 1);
      setProduct(result);
    }
    function onHandleAdd(product) {
        console.log(product);
        setProduct([...products, product]);
      }
    return (
        <Router>
            <Switch>
                <Route path="/admin/:path?" exact>
                    <MainAdmin>
                        <Switch>
                            <Route path='/admin' exact>
                                <Dashboard />
                            </Route>
                            <Route path='/admin/products'>
                                <ProductsManager products={products} onRemove={onHandleRemove} />
                            </Route>
                            <Route path='/admin/products/add'>
                                <AddProduct onAdd={onHandleAdd}/>
                            </Route>
                        </Switch>
                    </MainAdmin>
                </Route>
                <Route>
                    <Main>
                        <Switch>
                            <Route path="/" exact>
                                <Home />
                            </Route>
                            <Route path="/about">
                                <About />
                            </Route>
                        </Switch>
                    </Main>
                </Route>
            </Switch>
        </Router>
    )
}

Routers.propTypes = {

}

export default Routers
