import React, {useState} from 'react'
import PropTypes from 'prop-types'
import {useHistory} from 'react-router-dom'
import { useForm } from 'react-hook-form'

function AddProduct({onAdd}){  
    let history = useHistory();
    const [ValueInput, setValueInput] = useState({});
    function onHandleChange(e){
        const { name, image, price, value } = e.target;
        setValueInput({
            ...ValueInput,
            [name]: e.target.value,
            [image]: e.target.value,
            [price]: e.target.value
        });
    }
    function onSubmit(e){
        onAdd(ValueInput)
        history.push('/admin/products')
    }
    const { register, handleSubmit, watch, errors } = useForm();
    return (
        <div>
                <div className="content-wrapper">
                <div className="card">
                    <div className="card-header">
                        <h3 className="card-title">Thêm sản phẩm</h3>
                        <br />
                        <form >
                            <div className="form-group">
                                    <label htmlFor="inputProductName">Tên sản phẩm</label>
                                    <input ref={register({ required: true, minLength: 5 })} name="name" type="text" className="form-control" id="inputProductName" onChange={onHandleChange}/>
                                    {errors.name && <span style={{ color: "red" }}>Tên sản phẩm không được để trống, không dưới năm ký tự</span>}
                            </div>
                            <div className="form-group">
                                    <label htmlFor="inputProductImage">Ảnh sản phẩm</label>
                                    <input ref={register({ required: true })} name="image" type="file" className="form-control" id="inputProductImage" onChange={onHandleChange}/>
                                    {errors.image && <span style={{ color: "red" }}>Ảnh sản phẩm không được để trống</span>}
                            </div>
                            <div className="form-group">
                                    <label htmlFor="inputProductPrice">Gía sản phẩm</label>
                                    <input ref={register({ required: true, min: 0 })} name="price" type="text" className="form-control" id="inputProductPrice" onChange={onHandleChange}/>
                                    {errors.price && <span style={{ color: "red" }}>Gía sản phẩm không được để trống, giá sản phẩm phải lớn hơn không</span>}
                            </div>
                            <button onClick={handleSubmit(onSubmit)} type="submit" className="btn btn-primary">Submit</button>
                        </form>
                    </div>
                </div>
                </div>
            
        </div>
    )
}

AddProduct.propTypes = {

}

export default AddProduct
