import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { useHistory, useParams } from 'react-router-dom'

const EditProduct = ({ products, onEdit }) => {
    let history = useHistory();
    const { id } = useParams();
    const onSubmit = (event) => {
        event.preventDefault();
        const { name, image, price, Desc, value } = event.target;
        const params = {
            id: id,
            name: name.value,
            image: image.value,
            price: price.value,
            Desc: Desc.value,
        }
        onEdit(id, params);
        history.push("/admin/products");

    }
    return (
        <div>
            {products.map((product) => {
                if (product.id == id) {
                    return (
                        <div >
                            <section class="content">
                                <h3 className="card-title">Sửa sản phẩm</h3>
                                <form onSubmit={onSubmit}>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Tên sản phẩm</label>
                                        <input type="text"
                                            name="name"
                                            className="form-control"
                                            id="inputProductName"
                                            aria-describedby="emailHelp"
                                            defaultValue={product.name} />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Gía sản phẩm</label>
                                        <input type="number"
                                            name="price"
                                            className="form-control"
                                            id="inputProductName"
                                            aria-describedby="emailHelp"
                                            defaultValue={product.price} />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Ảnh sản phẩm</label>
                                        <input type="text"
                                            name="image"
                                            className="form-control"
                                            id="inputProductName"
                                            aria-describedby="emailHelp"
                                            defaultValue={product.image} />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Mô tả sản phẩm</label>
                                        <textarea className="form-control"
                                            name="Desc" id="inputProductName"
                                            defaultValue={product.Desc}
                                        />
                                    </div>
                                    <button type="submit" className="btn btn-primary">Submit</button>
                                </form>
                            </section>
                        </div>
                    );
                }
            })}


        </div>
    )
}

EditProduct.propTypes = {

}

export default EditProduct
